changeSet.sql:
create table ${schemaName}.movement_items ( id bigserial, movement_id bigint not null, material_id bigint not null, amount numeric(3, 2) not null, registration_date Date not null,
constraint pk_movement_items primary key(id), constraint fk_movements foreign key(movement_id) references dbapatient.movements(id) );
alter table dbapatient.movement_items add constraint fk_materials foreign key(material_id) references dbapatient.materials(id);
